import React, {useState, useEffect} from 'react';
import { View, Text, StatusBar, ScrollView, TouchableOpacity, Image, StyleSheet, FlatList } from 'react-native';
import {COLOURS, Items} from '../database/Database';
import Entypo from 'react-native-vector-icons/Entypo';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import { useSelector } from 'react-redux';

const Home = ({navigation}) => {
  const cartCount = useSelector(state => state.cart.quantity);
  
  const ProductCard = ({product}) => {
    return (
      <TouchableOpacity onPress={() => navigation.navigate('ProductInfo', {product: product})} style={{ width: '48%', marginVertical: 14 }}>
        <View style={{ width: '100%', height: 100, borderRadius: 10, backgroundColor: COLOURS.backgroundLight, position: 'relative', justifyContent: 'center', alignItems: 'center', marginBottom: 8 }}>
          <Image source={product.productImage} style={{ width: '80%', height: '80%', resizeMode: 'contain' }} />
        </View>
        <Text style={{ fontSize: 12, color: COLOURS.black, fontWeight: '600', marginBottom: 2 }}>{product.productName}</Text>
        <Text>â‚¬ {product.price}</Text>
      </TouchableOpacity>
    );
  };

  return (
    <View style={styles.container}>
      <StatusBar backgroundColor={COLOURS.white} barStyle="dark-content" />
      <ScrollView showsVerticalScrollIndicator={false}>
        <View style={{ width: '100%', flexDirection: 'row', justifyContent: 'space-between', padding: 16 }}>
          <TouchableOpacity>
          <Entypo name="shopping-bag" style={{ fontSize: 18, color: COLOURS.backgroundMedium, padding: 12, borderRadius: 10, backgroundColor: COLOURS.backgroundLight }}/>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => navigation.navigate('MyCart')}>
            <MaterialCommunityIcons name="cart" style={{ fontSize: 18, color: COLOURS.backgroundMedium, padding: 12, borderRadius: 10, borderWidth: 1, borderColor: COLOURS.backgroundLight }}/>
            {cartCount > 0 ? (
                  <View
                    style={{     
                      position: 'absolute',
                      backgroundColor: 'red',
                      width: 16,
                      height: 16,
                      borderRadius: 15 / 2,
                      right: 2,
                      top: +4,
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}>
                    <Text
                      style={{
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: "black",
                        fontSize: 10,
                      }}>
                      {cartCount}
                    </Text>
                  </View>
                ) : null}
          </TouchableOpacity>
        </View>
        <View style={{ marginBottom: 10, padding: 20}}>
          <Text style={{ fontSize: 26, color: COLOURS.black, fontWeight: '500', letterSpacing: 1, marginBottom: 10 }}>Eindwerk Mobile</Text>
          <Text style={{ fontSize: 14, color: COLOURS.black, fontWeight: '400', letterSpacing: 1, lineHeight: 24 }}>Webshop - Gemaakt door Gertjan Deschuytter</Text>
        </View>
        <View style={{ padding: 16 }}>
          <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' }}>
            <View style={{ flexDirection: 'row', alignItems: 'center'}}>
              <Text style={{ fontSize: 18, color: COLOURS.black, fontWeight: '500', letterSpacing: 1 }}>Producten</Text>
            </View>
          </View>
          <View style={{ flexDirection: 'row', flexWrap: 'wrap', justifyContent: 'space-around' }}>
            {Items.map(product => {
              return <ProductCard product={product} key={product.id} />;
            })}
          </View>
        </View>
      </ScrollView>
    </View>
  );
};

export default Home;

const styles = StyleSheet.create({
  container: {
    width: '100%',
    height: '100%',
    backgroundColor: COLOURS.white
  }
})
